import React, { useRef, useState, useEffect, useMemo } from 'react';
import { useFrame } from 'react-three-fiber';
import * as THREE from 'three';
import SphereBufferGeometry from './SphereBufferGeometry';

const NeonCursor = () => {
  const cursorRef = useRef();
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Créer l'objet d'uniformes personnalisé
  const uniforms = useMemo(() => ({
    emissive: { value: new THREE.Color(0x00ffff) },
  }), []);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  useFrame(() => {
    if (cursorRef.current) {
      cursorRef.current.position.set(
        (mousePosition.x / window.innerWidth) * 2 - 1,
        -(mousePosition.y / window.innerHeight) * 2 + 1,
        0
      );
    }
  });

  return (
    <mesh ref={cursorRef}>
      <SphereBufferGeometry args={[0.05, 32, 32]} />
      <meshBasicMaterial
        uniforms={uniforms}
        color={new THREE.Color(0x00ffff)} // Couleur cyan
        transparent
        opacity={0.7} // Opacité légèrement réduite
        emissiveIntensity={0.5} // Intensité d'émission
      />
    </mesh>
  );
};

export default NeonCursor;
