import React, { useRef, useEffect, useState } from 'react';
import './Serpentin.css'; // Assure-toi que le CSS est importé

const Serpentin = () => {
  const canvasRef = useRef(null);
  const [pointerEvents, setPointerEvents] = useState('none');

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const drawSerpentin = (x, y) => {
      if (!ctx) return;

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = '#FFD700'; // Jaune doré
      ctx.lineWidth = 4;
      ctx.beginPath();
      ctx.moveTo(x, y);
      ctx.lineTo(x + 100, y + 100); // Exemple de ligne
      ctx.stroke();
    };

    const handleMouseMove = (event) => {
      const x = event.clientX;
      const y = event.clientY;
      drawSerpentin(x, y);
    };

    const handleMouseEnter = () => setPointerEvents('auto');
    const handleMouseLeave = () => setPointerEvents('none');

    // Resize canvas when window resizes
    window.addEventListener('resize', resizeCanvas);
    resizeCanvas(); // Initial resize

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseenter', handleMouseEnter);
    document.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseenter', handleMouseEnter);
      document.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [pointerEvents]);

  return (
    <canvas
      ref={canvasRef}
      className="serpentin-canvas"
      style={{ pointerEvents }}
    ></canvas>
  );
};

export default Serpentin;
