import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame, extend } from '@react-three/fiber';
import { TubeGeometry, MeshBasicMaterial } from 'three';
import * as THREE from 'three';

// Enregistrer les objets personnalisés avec `extend`
extend({ TubeGeometry, MeshBasicMaterial });

const CurveNeonCursor = () => {
    const curveRef = useRef(new THREE.CatmullRomCurve3([]));
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

    useEffect(() => {
        const handleMouseMove = (e) => {
            setMousePosition({ x: e.clientX, y: e.clientY });
        };

        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    useFrame(() => {
        if (curveRef.current) {
            const newPoint = new THREE.Vector3(
                (mousePosition.x / window.innerWidth) * 2 - 1,
                -(mousePosition.y / window.innerHeight) * 2 + 1,
                0
            );

            if (curveRef.current.points.length >= 50) {
                curveRef.current.points.shift();
            }

            curveRef.current.points.push(newPoint);
            curveRef.current.points = [...curveRef.current.points];
        }
    });

    return (
        <mesh>
            <tubeGeometry args={[curveRef.current, 50, 0.02, 8, false]} />
            <meshBasicMaterial color="cyan" />
        </mesh>
    );
};

const App = () => {
    return (
        <Canvas>
            <CurveNeonCursor />
        </Canvas>
    );
};

export default App;
