import React, { useEffect, useRef } from 'react';
import './Serpentin.css';

const Serpentin = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Dessiner un rectangle simple pour tester
    ctx.fillStyle = 'red';
    ctx.fillRect(50, 50, 100, 100);
  }, []);

  return <canvas ref={canvasRef} width="100vw" height="100vh" className="serpentin-canvas" />;
};

export default Serpentin;
