import React, { useRef, useState, useEffect } from 'react';
import { useFrame } from 'react-three-fiber';
import * as THREE from 'three';

const CurveNeonCursor = () => {
    const curveRef = useRef();
    const pointsRef = useRef([]);
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const [isCurveInitialized, setIsCurveInitialized] = useState(false);

    useEffect(() => {
        const handleMouseMove = (e) => {
            setMousePosition({ x: e.clientX, y: e.clientY });
        };

        window.addEventListener('mousemove', handleMouseMove);

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    useFrame(() => {
        if (curveRef.current && pointsRef.current.length > 0 && isCurveInitialized) {
            const lastPoint = pointsRef.current[pointsRef.current.length - 1];
            lastPoint.x = (mousePosition.x / window.innerWidth) * 2 - 1;
            lastPoint.y = -(mousePosition.y / window.innerHeight) * 2 + 1;
            curveRef.current.needsUpdate = true;
        }
    });

    // Ajouter un nouveau point à la courbe toutes les 50 ms
    useEffect(() => {
        const timer = setInterval(() => {
            if (pointsRef.current.length < 50) {
                const point = new THREE.Vector2();
                pointsRef.current.push(point);
                if (curveRef.current) {
                    curveRef.current.getPoints().push(point);
                    curveRef.current.needsUpdate = true;
                }
            }
        }, 50);

        return () => {
            clearInterval(timer);
        };
    }, []);

    // Initialiser la courbe lors du premier rendu
    useEffect(() => {
        if (curveRef.current && !isCurveInitialized) {
            curveRef.current.getPoints().push(new THREE.Vector2());
            curveRef.current.needsUpdate = true;
            setIsCurveInitialized(true);
        }
    }, [isCurveInitialized]);

    return (
        <>
            <curve ref={curveRef}>
                <catmullRomCurve3 points={pointsRef.current} />
            </curve>
            <tubeGeometry attach="geometry" args={[curveRef.current, 64, 1, false]} />
            <meshBasicMaterial
                attach="material"
                color={new THREE.Color(0x00ffff)}

transparent opacity={0.7} side={THREE.DoubleSide} />
</>
);
};

export default CurveNeonCursor;
