import React from 'react';
import UserList from './components/UserList';
import UserForm from './components/UserForm';
import './App.css'; // Importez si vous utilisez des styles personnalisés ou Bootstrap

const App = () => {
    const addUser = (newUser) => {
        // Logique pour ajouter le nouvel utilisateur à la liste
        // Cela dépend de la manière dont vous gérez l'état dans votre application
        console.log('New user added:', newUser);
    };

    return (
        <div className="container">
            <div className="row mt-4">
                <div className="col-md-6">
                    <UserList />
                </div>
                <div className="col-md-6">
                    <UserForm addUser={addUser} />
                </div>
            </div>
        </div>
    );
};

export default App;
