// src/components/NeonCursor.js
import React, { useEffect } from 'react';
import './neonCursor.css';

const NeonCursor = () => {
  useEffect(() => {
    // Importer le module dynamique pour éviter les problèmes avec le bundler
    const importNeonCursor = async () => {
      const { neonCursor } = await import('https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js');
      
      // Créer et ajouter le canvas
      const canvas = document.createElement('canvas');
      canvas.id = 'neon-canvas'; // Assurez-vous d'utiliser un ID unique
      document.getElementById('app2').appendChild(canvas);

      // Initialiser neonCursor
      neonCursor({
        el: canvas,
        shaderPoints: 16,
        curvePoints: 80,
        curveLerp: 0.5,
        radius1: 5,
        radius2: 30,
        velocityTreshold: 10,
        sleepRadiusX: 100,
        sleepRadiusY: 100,
        sleepTimeCoefX: 0.0025,
        sleepTimeCoefY: 0.0025
      });
    };

    importNeonCursor();

    // Nettoyage à la désactivation du composant
    return () => {
      const canvas = document.getElementById('neon-canvas');
      if (canvas) {
        canvas.remove();
      }
    };
  }, []);

  return null; // Ce composant n'affiche rien directement
};

export default NeonCursor;
