import React from 'react';
import * as THREE from 'three';
import { extend, useThree } from 'react-three-fiber';

class SphereGeometry extends React.Component {
  constructor(props) {
    super(props);
    const { args = [] } = props; // Ajouter une valeur par défaut pour args
    this.geometry = new THREE.SphereGeometry(...args);
  }

  componentDidUpdate(prevProps) {
    const { args = [] } = this.props; // Ajouter une valeur par défaut pour args
    if (prevProps.args !== this.props.args) {
      this.geometry.dispose();
      this.geometry = new THREE.SphereGeometry(...args);
    }
  }

  render() {
    return null;
  }
}

extend({ SphereGeometry });

const SphereBufferGeometryComponent = (props) => {
  const { gl } = useThree();
  return <sphereGeometry attach="geometry" args={props.args} />;
};

export default SphereBufferGeometryComponent;
