import React, { useEffect } from 'react';
import { neonCursor } from 'https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js';
import './neonCursor.css'; // Assurez-vous que le chemin est correct

const NeonCursor = () => {
  useEffect(() => {
    // Crée un canvas si ce n'est pas déjà fait
    const canvas = document.createElement('canvas');
    canvas.id = 'neon-canvas'; // Assurez-vous d'utiliser un ID unique
    document.body.appendChild(canvas);

    // Initialise le curseur néon
    neonCursor({
      el: canvas,
      shaderPoints: 16,
      curvePoints: 80,
      curveLerp: 0.5,
      radius1: 5,
      radius2: 30,
      velocityTreshold: 10,
      sleepRadiusX: 100,
      sleepRadiusY: 100,
      sleepTimeCoefX: 0.0025,
      sleepTimeCoefY: 0.0025
    });
    
    // Nettoie le canvas à la destruction du composant
    return () => {
      document.body.removeChild(canvas);
    };
  }, []);

  return null; // Ce composant n'affiche rien mais initialise le curseur néon
};

export default NeonCursor;
