import React, { useEffect } from 'react';
import { neonCursor } from 'https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js';
import './neonCursor.css'; // Assurez-vous que le chemin est correct

const NeonCursor = () => {
  useEffect(() => {
    // Vérifiez si neonCursor est bien défini
    console.log('neonCursor:', neonCursor);

    // Crée un canvas si ce n'est pas déjà fait
    const canvas = document.createElement('canvas');
    canvas.id = 'neon-canvas'; // Assurez-vous d'utiliser un ID unique
    document.body.appendChild(canvas);

    // Assurez-vous que le canvas est correctement stylisé
    canvas.style.position = 'absolute';
    canvas.style.bottom = '0';
    canvas.style.left = '0';
    canvas.style.width = '100%';
    canvas.style.height = '500px';
    canvas.style.backgroundColor = 'black'; // Pour voir le canvas si le curseur ne fonctionne pas encore

    // Initialise le curseur néon
    const initializeCursor = () => {
      console.log('Initializing neonCursor'); // Ajoutez un log ici pour confirmer l'appel
      neonCursor({
        el: canvas,
        shaderPoints: 16,
        curvePoints: 80,
        curveLerp: 0.5,
        radius1: 5,
        radius2: 30,
        velocityTreshold: 10,
        sleepRadiusX: 100,
        sleepRadiusY: 100,
        sleepTimeCoefX: 0.0025,
        sleepTimeCoefY: 0.0025
      });
    };

    // Attendez que le canvas soit prêt avant d'initialiser
    const checkCanvasReady = () => {
      console.log(`Canvas dimensions: ${canvas.width} x ${canvas.height}`); // Vérifiez les dimensions du canvas
      if (canvas.width > 0 && canvas.height > 0) {
        initializeCursor();
      } else {
        requestAnimationFrame(checkCanvasReady); // Vérifiez à nouveau si le canvas est prêt
      }
    };

    checkCanvasReady();

    // Nettoie le canvas à la destruction du composant
    return () => {
      document.body.removeChild(canvas);
    };
  }, []);

  return null; // Ce composant n'affiche rien mais initialise le curseur néon
};

export default NeonCursor;
