// src/components/NeonCursor.js
import React, { useEffect } from 'react';
import { neonCursor } from 'https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js';
import './neonCursor.css'; // Assurez-vous que le chemin est correct

const NeonCursor = () => {
  useEffect(() => {
    console.log('Initializing NeonCursor...');
    const element = document.querySelector('.main-menu');
    
    if (element) {
      neonCursor({
        el: element,
        shaderPoints: 16,
        curvePoints: 80,
        curveLerp: 0.5,
        radius1: 5,
        radius2: 30,
        velocityTreshold: 10,
        sleepRadiusX: 100,
        sleepRadiusY: 100,
        sleepTimeCoefX: 0.0025,
        sleepTimeCoefY: 0.0025
      });
      console.log('NeonCursor initialized');
    } else {
      console.warn("Element '.main-menu' not found. Ensure it's in the DOM.");
    }
  }, []);

  return null; // Ce composant n'affiche rien mais initialise le curseur néon
};

export default NeonCursor;
