import React, { useEffect } from 'react';
import './neonCursor.css';

const NeonCursor = () => {
  useEffect(() => {
    const importNeonCursor = async () => {
      const { neonCursor } = await import('https://unpkg.com/threejs-toys@0.0.8/build/threejs-toys.module.cdn.min.js');
      
      const canvas = document.createElement('canvas');
      canvas.id = 'neon-canvas'; // Assurez-vous d'utiliser un ID unique
      document.body.appendChild(canvas); // Attache le canvas au body

      neonCursor({
        el: canvas,
        shaderPoints: 16,
        curvePoints: 80,
        curveLerp: 0.5,
        radius1: 5,
        radius2: 30,
        velocityTreshold: 10,
        sleepRadiusX: 100,
        sleepRadiusY: 100,
        sleepTimeCoefX: 0.0025,
        sleepTimeCoefY: 0.0025
      });
    };

    importNeonCursor();

    return () => {
      const canvas = document.getElementById('neon-canvas');
      if (canvas) {
        canvas.remove();
      }
    };
  }, []);

  return null; // Pas besoin de rendre un conteneur ici
};

export default NeonCursor;
