// UserList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/users');
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const handleEdit = async (id) => {
        // Logique pour l'édition d'un utilisateur
        console.log('Edit user:', id);
    };

    const handleDelete = async (id) => {
        // Logique pour la suppression d'un utilisateur
        console.log('Delete user:', id);
    };

    return (
        <div>
            <h2>User Grid</h2>
            <div className="row">
                {users.slice(0, 10).map(user => (
                    <div key={user._id} className="col-md-3 mb-4">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">{user.name}</h5>
                                <p className="card-text">{user.email}</p>
                                <button className="btn btn-primary mr-2" onClick={() => handleEdit(user._id)}>Edit</button>
                                <button className="btn btn-danger" onClick={() => handleDelete(user._id)}>Delete</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default UserList;
