// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserForm from './components/UserForm';
import UserEdit from './components/UserEdit';
import UserList from './components/UserList';

const App = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users');
                setUsers(response.data);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };
        fetchUsers();
    }, []);

    const addUser = async (user) => {
        try {
            const response = await axios.post('http://localhost:5000/api/users', user);
            setUsers([...users, response.data]);
        } catch (error) {
            console.error('Error adding user:', error);
        }
    };

    const updateUser = async (updatedUser) => {
        try {
            const response = await axios.put(`http://localhost:5000/api/users/${updatedUser._id}`, updatedUser);
            setUsers(users.map(user => (user._id === updatedUser._id ? response.data : user)));
            setSelectedUser(null); // Clear the selected user after update
        } catch (error) {
            console.error('Error updating user:', error);
        }
    };

    const deleteUser = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/users/${id}`);
            setUsers(users.filter(user => user._id !== id));
        } catch (error) {
            console.error('Error deleting user:', error);
        }
    };

    return (
        <div>
            <UserForm addUser={addUser} />
            {selectedUser && (
                <UserEdit
                    user={selectedUser}
                    onUpdate={(updatedUser) => {
                        updateUser(updatedUser);
                    }}
                />
            )}
            <UserList
                users={users}
                editUser={(id) => {
                    const user = users.find(user => user._id === id);
                    setSelectedUser(user);
                }}
                deleteUser={deleteUser}
            />
        </div>
    );
};

export default App;
